package ej;

public class Tableau {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] tableauEntier = {1, 2, 3, 4, 5, 6, 7, 8};
		
		int a = tableauEntier[3] ;
		
		int b = tableauEntier[2];
		
		System.out.println(a);
		System.out.println(b);
		
		char[] tableauChar = new char[3];    //j'ajoute 3 case dans le tableau 
		
		tableauChar[0] = 'a';
		tableauChar[1] = 'b';
		tableauChar[2] = 'c';
		
		char test = tableauChar[0];
		
		System.out.println(test);
		System.out.println(tableauChar);
		
	
	}

}
